import React, { useContext, useEffect, useState } from 'react';
//Import photos
import Photo from '../img/retrato1.jpeg';
import Edit from '../img/edit.png';
import Delete from '../img/delete.png';
//import link functionality
import { Link, useLocation } from 'react-router-dom';
//import menu
import Menu from '../partials/Menu';
//import axios
import axios from 'axios';
//import moment
import moment from 'moment';
//import context
import { AuthContext } from '../context/authContext';

const Single = () => {
  const [post, setPost] = useState({});

  const location = useLocation();

  const postId = location.pathname.split('/')[2];

  const { currentUser } = useContext(AuthContext);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get(
          `http://localhost:8800/api/posts/${postId}`
        );
        setPost(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchData();
  }, [postId]);

  return (
    <div className="single">
      <div className="content">
        <img src={post?.img} alt="" />
        <div className="user">
          <img src={Photo} alt="" />
          <div className="info">
            <span>{post.username}</span>
            <p>Posted {moment(post.date).fromNow()}</p>
          </div>
          {currentUser.username === post.username && (
            <div className="edit">
              <Link to={`/write?edit=2`}>
                <img src={Edit} alt="" />
              </Link>
              <img src={Delete} alt="" />
            </div>
          )}
        </div>
        <h1>Lorem ipsum dolor sit amet consectetur adipisicing elit.</h1>
        <p>
          <p>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit. Quo eaque
            dolore beatae magnam mollitia pariatur nihil consequuntur numquam.
            Minima animi consequatur porro in esse atque nesciunt dignissimos
            ullam ipsam commodi.
            <br />
