import React from 'react';
//Import photos
import Photo from '../img/retrato1.jpeg';
import Edit from '../img/edit.png';
import Delete from '../img/delete.png';
//import link functionality
import { Link } from 'react-router-dom';

const Single = () => {
  return (
 <div className='single'>
  <div className="content">
    <img src="https://images.pexels.com/photos/14980647/pexels-photo-14980647.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=2" alt="" />
  <div className="user">
    <img src={Photo} alt="" />
    <div className="info">
      <span>Wilson Rodriguez</span>
      <p>Posted 2 days ago</p>
    </div>
    <div className="edit">
      <Link to={`/write?edit=2`}> <img src={Edit} alt="" /> </Link>
     
      <img src={Delete} alt="" />
    </div>
  </div>
  </div>
  <div className="menu">m</div>
 </div>

  )
}

export default Single
