import React, { useState } from 'react';
//import link functionality
import { Link, useNavigate } from 'react-router-dom';
//import axios
import axios from 'axios';

const Login = () => {
  //Communication with BACKEND
  const [inputs, setInputs] = useState({
    username: '',
    password: '',
  });

  //const to catch error
  const [err, SetError] = useState(null);

  //This navigae const is used to send users to login after they get registered
  const navigate = useNavigate();

  //Store data in variables
  const handleChange = (e) => {
    setInputs((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:8800/api/auth/register', inputs);
      navigate('/login');
    } catch (err) {
      SetError(err.response.data);
    }
  };

  console.log(inputs);

  return (
    <div className="auth">
      <h1>Login</h1>
      <form>
        <input required type="text" placeholder="username"></input>
        <input required type="password" placeholder="password"></input>
        <button>Login</button>
        <p>Error Message</p>
        <span>
          Don't you have an account? <Link to="/register">Register here</Link>
        </span>
      </form>
    </div>
  );
};

export default Login;
